import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [jobs, setJobs] = useState([]);
  const [form, setForm] = useState({
    company: '',
    role: '',
    status: 'Applied',
    date: '',
    link: ''
  });

  const fetchJobs = () => {
    axios.get('https://your-backend-url/jobs')
      .then(res => setJobs(res.data));
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const addJob = (e) => {
    e.preventDefault();
    axios.post('https://your-backend-url/jobs', form).then(() => {
      fetchJobs();
      setForm({ company: '', role: '', status: 'Applied', date: '', link: '' });
    });
  };

  const deleteJob = (id) => {
    axios.delete(`https://your-backend-url/jobs/${id}`).then(fetchJobs);
  };

  const updateStatus = (id, status) => {
    axios.put(`https://your-backend-url/jobs/${id}`, { status }).then(fetchJobs);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Student Job Tracker</h1>

      <form onSubmit={addJob}>
        <input name="company" placeholder="Company" value={form.company} onChange={handleChange} />
        <input name="role" placeholder="Role" value={form.role} onChange={handleChange} />
        <select name="status" value={form.status} onChange={handleChange}>
          <option>Applied</option>
          <option>Interview</option>
          <option>Offer</option>
          <option>Rejected</option>
        </select>
        <input name="date" type="date" value={form.date} onChange={handleChange} />
        <input name="link" placeholder="Link" value={form.link} onChange={handleChange} />
        <button type="submit">Add Job</button>
      </form>

      <ul>
        {jobs.map(job => (
          <li key={job._id}>
            <strong>{job.company}</strong> - {job.role} [{job.status}]
            <br />
            <a href={job.link} target="_blank" rel="noreferrer">Link</a> | {new Date(job.date).toLocaleDateString()}
            <br />
            <button onClick={() => updateStatus(job._id, 'Interview')}>Interview</button>
            <button onClick={() => updateStatus(job._id, 'Offer')}>Offer</button>
            <button onClick={() => updateStatus(job._id, 'Rejected')}>Rejected</button>
            <button onClick={() => deleteJob(job._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
