import React, { useState, useEffect } from "react";
import axios from "axios";
import "./"

function App() {
  const [jobs, setJobs] = useState([]);
  const [form, setForm] = useState({
    company: "",
    role: "",
    status: "Applied",
    date: "",
    link: ""
  });

  const API_URL = "http://localhost:5000/jobs"; // Later we'll replace this with the Render URL

  // Load jobs when app starts
  useEffect(() => {
    getJobs();
  }, []);

  const getJobs = async () => {
    const res = await axios.get(API_URL);
    setJobs(res.data);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post(API_URL, form);
    setForm({ company: "", role: "", status: "Applied", date: "", link: "" });
    getJobs();
  };

  const deleteJob = async (id) => {
    await axios.delete(`${API_URL}/${id}`);
    getJobs();
  };

  const updateStatus = async (id, newStatus) => {
    await axios.put(`${API_URL}/${id}`, { status: newStatus });
    getJobs();
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>üéì Student Job Tracker</h1>

      <form onSubmit={handleSubmit}>
        <input name="company" placeholder="Company" value={form.company} onChange={handleChange} required />
        <input name="role" placeholder="Role" value={form.role} onChange={handleChange} required />
        <select name="status" value={form.status} onChange={handleChange}>
          <option>Applied</option>
          <option>Interview</option>
          <option>Offer</option>
          <option>Rejected</option>
        </select>
        <input name="date" type="date" value={form.date} onChange={handleChange} required />
        <input name="link" placeholder="Link" value={form.link} onChange={handleChange} required />
        <button type="submit">Add Job</button>
      </form>

      <hr />

      <h2>üìã Job Applications</h2>
      <ul>
        {jobs.map((job) => (
          <li key={job._id}>
            <strong>{job.company}</strong> - {job.role} - {job.status}
            <br />
            <a href={job.link} target="_blank" rel="noreferrer">üîó Link</a> | üìÖ {new Date(job.date).toLocaleDateString()}
            <br />
            <button onClick={() => updateStatus(job._id, "Interview")}>Interview</button>
            <button onClick={() => updateStatus(job._id, "Offer")}>Offer</button>
            <button onClick={() => updateStatus(job._id, "Rejected")}>Reject</button>
            <button onClick={() => deleteJob(job._id)}>‚ùå Delete</button>
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
